<script>
document.addEventListener("DOMContentLoaded", function () {
  // Initialize phone input
  const phoneInput = document.querySelector("#phone-number");
  const countryCodeInput = document.querySelector("#phone-number-country-code");
  let iti; // Will hold the intlTelInput instance

  if (phoneInput) {
    iti = window.intlTelInput(phoneInput, {
      initialCountry: "ae",
      separateDialCode: true,
      utilsScript: "https://cdnjs.cloudflare.com/ajax/libs/intl-tel-input/17.0.19/js/utils.js"
    });

    phoneInput.addEventListener("countrychange", function () {
      const countryData = iti.getSelectedCountryData();
      countryCodeInput.value = countryData.dialCode;
    });

    const initialCountryData = iti.getSelectedCountryData();
    if (initialCountryData) {
      countryCodeInput.value = initialCountryData.dialCode;
    }
  }

  // Auto-fill property_name from dropdown
  const interestedInSelect = document.getElementById('interested-in-home');
  const propertyNameField = document.getElementById('property_name');

  if (interestedInSelect && propertyNameField) {
    interestedInSelect.addEventListener('change', function () {
      propertyNameField.value = this.value;
    });
    
    // Set initial value if one is selected
    if (interestedInSelect.value) {
      propertyNameField.value = interestedInSelect.value;
    }
  }

  // Form validation
  const form = document.querySelector('.register-interest-form-section form');
  
  if (form) {
    const fields = form.querySelectorAll('input[required]:not([type="hidden"]), select[required]');
    
    // Real-time validation
    fields.forEach(field => {
      field.addEventListener('input', () => validateField(field));
      field.addEventListener('change', () => validateField(field));
      field.addEventListener('blur', () => validateField(field));
    });

    // Form submission handler
    form.addEventListener('submit', function (e) {
      let isValid = true;
      
      // Validate all fields
      fields.forEach(field => {
        if (!validateField(field)) {
          isValid = false;
        }
      });
      
      // Special phone number validation
      if (phoneInput) {
        const phoneValue = phoneInput.value.trim();
        const formGroup = phoneInput.closest('.form-group');
        
        formGroup.classList.remove('field-error');
        formGroup.removeAttribute('data-error');
        
        if (!phoneValue) {
          formGroup.classList.add('field-error');
          formGroup.setAttribute('data-error', 'Phone number is required');
          isValid = false;
        } else if (!iti.isValidNumber()) {
          formGroup.classList.add('field-error');
          formGroup.setAttribute('data-error', 'Please enter a valid phone number');
          isValid = false;
        } else {
          // Format the phone number correctly before submission
          phoneInput.value = iti.getNumber();
        }
      }
      
      if (!isValid) {
        e.preventDefault();
        // Scroll to first error
        const firstError = form.querySelector('.field-error');
        if (firstError) {
          firstError.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
      }
      // If valid, form will submit normally
    });

    function validateField(field) {
      const formGroup = field.closest('.form-group');
      if (!formGroup) return true;

      formGroup.classList.remove('field-error');
      formGroup.removeAttribute('data-error');

      if (field.required && !field.value.trim()) {
        formGroup.classList.add('field-error');
        formGroup.setAttribute('data-error', 'This field is required');
        return false;
      }

      if (field.value.trim() && field.pattern) {
        const regex = new RegExp(field.pattern);
        if (!regex.test(field.value)) {
          formGroup.classList.add('field-error');
          formGroup.setAttribute('data-error', field.title || 'Please enter a valid value');
          return false;
        }
      }

      if (field.type === 'email' && field.value.trim() && !field.validity.valid) {
        formGroup.classList.add('field-error');
        formGroup.setAttribute('data-error', 'Please enter a valid email address');
        return false;
      }

      return true;
    }
  }
});
</script>
<script>
document.addEventListener('DOMContentLoaded', function() {
    const form = document.querySelector('.register-interest-form-section form');
    
    // Add real-time validation
    const fields = form.querySelectorAll('input[required], select[required]');
    fields.forEach(field => {
        field.addEventListener('input', function() {
            validateField(this);
        });
        field.addEventListener('change', function() {
            validateField(this);
        });
        field.addEventListener('blur', function() {
            validateField(this);
        });
    });
    
    form.addEventListener('submit', function(e) {
        e.preventDefault();
        
        // Validate all fields
        let isValid = true;
        fields.forEach(field => {
            if (!validateField(field)) {
                isValid = false;
            }
        });
        
        if (isValid) {
            // Submit the form
            form.submit();
        }
    });
    
    function validateField(field) {
        const formGroup = field.closest('.form-group');
        if (!formGroup) return true;
        
        // Clear previous errors
        formGroup.classList.remove('field-error');
        formGroup.removeAttribute('data-error');
        
        // Check required field
        if (field.required && !field.value.trim()) {
            formGroup.classList.add('field-error');
            formGroup.setAttribute('data-error', 'This field is required');
            return false;
        }
        
        // Check pattern validation
        if (field.value.trim() && field.pattern) {
            const regex = new RegExp(field.pattern);
            if (!regex.test(field.value)) {
                formGroup.classList.add('field-error');
                formGroup.setAttribute('data-error', field.title || 'Please enter a valid value');
                return false;
            }
        }
        
        // Special validation for email
        if (field.type === 'email' && field.value.trim() && !field.validity.valid) {
            formGroup.classList.add('field-error');
            formGroup.setAttribute('data-error', 'Please enter a valid email address');
            return false;
        }
        
        return true;
    }
    
    // Auto-populate property_name if interested_in changes
    const interestedInSelect = document.getElementById('interested-in-home');
    const propertyNameField = document.getElementById('property_name');
    
    if (interestedInSelect && propertyNameField) {
        interestedInSelect.addEventListener('change', function() {
            propertyNameField.value = this.value;
        });
    }
});
</script>
